#define _CRT_SECURE_NO_WARNINGS#include <stdio.h>#define MAX 100int main(void) {	int T;	scanf("%d", &T);	for (int t = 0; t < T; t++) {		int candy, re;		scanf("%d %d", &candy, &re);		int height[MAX];		int max = 0;		for (int i = 0; i < candy; i++) {			scanf("%d", &height[i]);			if (height[i] > max) {				max = height[i];			}		}		int low = 0, high = max;		int result = 0;		while (low <= high) {			int a = (low + high) / 2;			long long total = 0;			for (int i = 0; i < candy; i++) {				if (height[i] > a) {					total += height[i] - a;				}			}			if (total >= re) {				result = a;				low = a + 1;			}			else {				high = a - 1;			}		}		printf("%d\n", result);	}	return 0;}